import request from "@/utils/request"; // 请根据实际请求方式调整

export default {
  name: "BookList",
  data() {
    return {
      tableData: [],
      // 存储书籍数据
      total: 0,
      // 数据总数
      params: {
        pageNum: 1,
        pageSize: 6
      },
      filters: {
        account: "",
        // 用户账号
        bookNo: "",
        // 书籍编号
        username: "",
        // 用户名
        bookName: "",
        // 图书名称
        dueDate: "",
        // 借书到期时间
        sortOrder: "ascending" // 排序顺序
      },
      autoSearch: false // 控制是否开启自动搜索
    };
  },
  methods: {
    // 加载数据
    async load() {
      try {
        const res = await request.get("/Book/Page", {
          params: {
            ...this.params,
            ...this.filters
          }
        });
        if (res.code === "200") {
          this.tableData = res.data.list;
          this.total = res.data.total;
        } else {
          this.$message.error("加载数据失败");
        }
      } catch (error) {
        this.$message.error("加载数据失败");
      }
    },
    // 处理输入变化并自动搜索
    handleInput() {
      if (this.autoSearch) {
        this.params.pageNum = 1;
        this.load();
      }
    },
    // 手动搜索按钮点击事件
    handleSearch() {
      this.params.pageNum = 1;
      this.load();
    },
    // 重置搜索条件
    handleReset() {
      this.filters = {
        account: "",
        bookNo: "",
        username: "",
        bookName: "",
        dueDate: "",
        sortOrder: "ascending"
      };
      this.params.pageNum = 1;
      this.load();
    },
    // 处理排序变化
    handleSortChange({
      prop,
      order
    }) {
      this.filters.sortOrder = order === "ascending" ? "ascending" : "descending";
      this.load();
    },
    // 分页改变
    handlePageChange(page) {
      this.params.pageNum = page;
      this.load();
    },
    // 编辑操作
    handleEdit(row) {
      this.editBook = {
        ...row
      }; // 复制选中的书籍数据到编辑模型
      this.editDialogVisible = true; // 显示修改对话框
    },
    // 删除操作
    async handleDelete(row) {
      try {
        const res = await request.delete(`/Book/Delete/${row.bookNo}`);
        if (res.code === "200") {
          this.$message.success("删除成功");
          this.load(); // 刷新数据
        } else {
          this.$message.error("删除失败");
        }
      } catch (error) {
        this.$message.error("删除失败");
      }
    }
  },
  created() {
    this.load(); // 初始化加载数据
  }
};
