import request from "@/utils/request"; // 请根据你的实际请求方式调整

export default {
  name: "BorrowList",
  data() {
    return {
      tableData: [],
      // 存储借阅数据
      total: 0,
      // 数据总数
      params: {
        pageNum: 1,
        pageSize: 6
      },
      filters: {
        name: "",
        account: "",
        username: "",
        dueDate: []
      },
      autoSearch: false,
      // 控制是否开启自动搜索
      addDialogVisible: false,
      // 控制添加借阅对话框显示与否
      editDialogVisible: false,
      // 控制编辑借阅对话框显示与否
      newBorrow: {
        name: "",
        account: "",
        username: "",
        phone: "",
        dueDate: ""
      },
      editBorrow: {
        id: null,
        name: "",
        account: "",
        username: "",
        phone: "",
        dueDate: ""
      }
    };
  },
  methods: {
    // 加载数据
    async load() {
      try {
        const res = await request.get("/Borrow/Page", {
          params: {
            ...this.params,
            ...this.filters
          }
        });
        if (res.code === "200") {
          this.tableData = res.data.list;
          this.total = res.data.total;
        } else {
          this.$message.error("加载数据失败");
        }
      } catch (error) {
        this.$message.error("加载数据失败");
      }
    },
    // 处理输入变化并自动搜索
    handleInput() {
      if (this.autoSearch) {
        this.params.pageNum = 1;
        this.load();
      }
    },
    // 手动搜索按钮点击事件
    handleSearch() {
      this.params.pageNum = 1;
      this.load();
    },
    // 重置搜索条件
    handleReset() {
      this.filters = {
        name: "",
        account: "",
        username: "",
        dueDate: []
      };
      this.params.pageNum = 1;
      this.load();
    },
    // 分页变化
    handlePageChange(page) {
      this.params.pageNum = page;
      this.load();
    },
    // 编辑借阅记录
    handleEdit(row) {
      this.editBorrow = {
        ...row
      };
      this.editDialogVisible = true;
    },
    // 保存编辑的借阅记录
    async handleEditBorrow() {
      const res = await request.post(`/Borrow/Edit/${this.editBorrow.id}`, this.editBorrow);
      if (res.code === "200") {
        this.$message.success("借阅记录已更新");
        this.load();
        this.editDialogVisible = false;
      } else {
        this.$message.error("更新借阅记录失败");
      }
    },
    // 删除借阅记录
    async handleDelete(row) {
      try {
        const res = await request.delete(`/Borrow/Delete/${row.id}`);
        if (res.code === "200") {
          this.$message.success("借阅记录已删除");
          this.load();
        } else {
          this.$message.error("删除借阅记录失败");
        }
      } catch (error) {
        this.$message.error("删除借阅记录失败");
      }
    },
    // 添加借阅记录
    async handleAddBorrow() {
      const res = await request.post("/Borrow/Add", this.newBorrow);
      if (res.code === "200") {
        this.$message.success("借阅记录已添加");
        this.load();
        this.addDialogVisible = false;
      } else {
        this.$message.error("添加借阅记录失败");
      }
    },
    // 打开添加借阅对话框
    openAddDialog() {
      this.newBorrow = {
        name: "",
        account: "",
        username: "",
        phone: "",
        dueDate: ""
      };
      this.addDialogVisible = true;
    }
  },
  mounted() {
    this.load();
  }
};
